mapper.select:
select * from orders


        and number like concat('%',#{number},'%')


        and phone like concat('%',#{phone},'%')


        and user_id = #{userId}


        and status = #{status}


        and order_time >= #{beginTime}


        and order_time <= #{endTime}


order by order_time desc

select sum(amount) from orders


        and status = #{status}


        and order_time >= #{begin}


        and order_time <= #{end}

select count(id) from orders


        and status = #{status}


        and order_time >= #{begin}


        and order_time <= #{end}

select od.name name,sum(od.number) number from order_detail od ,orders o where od.order_id = o.id and o.status = 5
and order_time >= #{begin}
and order_time <= #{end}
group by name order by number desc limit 0, 10

select * from orders


        AND status = #{status}


        AND order_time BETWEEN #{beginTime} AND #{endTime}


        AND user_id = #{userId}



        AND number LIKE CONCAT('%', #{number}, '%')


        AND phone = #{phone}


order by order_time

select count(id) from orders


        AND status = #{status}


        AND order_time BETWEEN #{begin} AND #{end}

select name,count(*) as number from orders o left outer join order_detail od on o.id = od.order_id
AND order_time BETWEEN #{begin} AND #{end}
group by od.name order by number desc limit 0,10
